import React, {Component} from 'react';
import './App.css';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom';

import View from './home/View';
import Register from './new/Register';

class Nav extends Component {
  render() {
    return (
        /*
        <Router>
            <div className="Nav">
                <nav className="navbar navbar-light bg-light">
                    <a className="navbar-brand" href="#">
                        <img src="../logo.svg" width="50" height="50" alt="penguin"/>
                    </a>
                    <div className="navbar-nav">
                        <Link to="/home"><a className="nav-item nav-link">Home</a></Link>
                        <Link to="/new"><a className="nav-item nav-link">New</a></Link>
                    </div>
                </nav>
            </div>
            <Route path="/home" component={App} />
            <Route path="/new" component={Register} />
        </Router>

         */
        <div className="Nav">
            <nav className="navbar navbar-light bg-light">
                <a className="navbar-brand" href="#">
                    <img src="../logo.svg" width="50" height="50" alt="penguin"/>
                </a>
            </nav>
        </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      list: null
    };
  };

  componentDidMount() {
    fetch('http://localhost:9000/api/students')
        .then(response => response.json())
        .then(result =>  this.setState({list: result}))
        .catch(error => error);
  }

  render() {

    {if (!this.state.list) return <Nav/>; }

    return (
        <div className="App">
          <Nav/>
          <View list={this.state.list}/>
        </div>
    );
  }
}

export default App;
