import React, { Component } from 'react';
import Select from 'react-select';

class View extends Component {
    constructor(props) {
        super(props);

        this.state = {
            nationalities: [],
            results: [],
        }
    }

    componentDidMount() {
        this.setState( {nationalities: [... new Set(this.props.list.map(item => item.nationality))]});
        this.setState({value: this.state.nationalities[0]});
    }

    filterList(e) {
        this.setState({results: this.props.list.filter})
    }

    render() {

        return (
            <div className="View">
                <div className="container">
                    <div className="row">
                        <div className="form">
                            <select id="dropdown" onChange={this.filterList} className="form-control form-control-lg">
                                {this.state.nationalities.map(function (item) {
                                    return (
                                        <option value={item}>{item}</option>
                                    );
                                })}
                            </select>
                        </div>
                    </div>
                </div>
                {this.state.results.map(function (item) {
                    return (
                        <h5 className="card-title">{item.firstName} {item.lastName} ({item.age})</h5>
                    );
                })
                }
            </div>
        );
    }
}

export default View;
