import React, {Component} from 'react';
import { Switch, Route, BrowserRouter as Router } from 'react-router-dom';
import Register from './new/Register';

import './App.css';

import View from './home/View';

class Nav extends Component {
  render() {
    return (
        <div className="Nav">
            <nav className="navbar navbar-light bg-light">
                <a className="navbar-brand" href="#">
                    <img src="../logo.svg" width="50" height="50" alt="penguin"/>
                </a>
            </nav>
        </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      list: null
    };
  };

  componentDidMount() {
    fetch('http://localhost:9000/api/students')
        .then(response => response.json())
        .then(result =>  this.setState({list: result}))
        .catch(error => error);
  }

  render() {

    {if (!this.state.list) return <Nav/>; }

    return (
        <Router>
            <Nav />
            <div className="container">
                <Route path="/new" exact component={Register} />
                <Route path="/home" render={() => <View list={this.state.list}/>} />
            </div>
        </Router>
    );
  }
}

export default App;
