import React, { Component } from 'react';
import axios from 'axios';

class Register extends Component {
    constructor(props) {
        super(props);

        this.createStudent = this.createStudent.bind(this);
    }

    createStudent(e) {
        e.preventDefault();
        const student = new FormData(e.target);

        fetch('http://localhost:9000/api/students', {
            method: 'POST',
            headers: {'Content-Type':'application/json'},
            body: student,
        });
    }

    render() {
        return (
            <div className="Register">
                <div className="container">
                    <form onSubmit={this.createStudent}>
                        <div className="form-group row">
                            <label className="col-sm-2 col-form-label">ID:</label>
                            <div className="col-sm-10">
                                <input className="form-control" id="id" placeholder="id"/>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-2 col-form-label">First Name:</label>
                            <div className="col-sm-10">
                                <input className="form-control" id="firstName" placeholder="First Name"/>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-2 col-form-label">Last Name:</label>
                            <div className="col-sm-10">
                                <input className="form-control" id="lastName" placeholder="Last Name"/>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-2 col-form-label">Age:</label>
                            <div className="col-sm-10">
                                <input className="form-control" id="age" placeholder="Age"/>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-2 col-form-label">Nationality:</label>
                            <div className="col-sm-10">
                                <input className="form-control" id="nationality" placeholder="Nationality"/>
                            </div>
                        </div>
                        <button type="submit" method='post' className="btn btn-primary">Create</button>
                    </form>
                </div>
            </div>
        )
    }
}

export default Register;
